import React, { useState } from 'react';
import { createAuthor } from './.././../../api/author/command'; 

const AuthorCreateForm = () => {
  const [form, setForm] = useState({
    username: '',
    Lastname: '',
    email: '',
    name: '',
    phone: '',
    address: '',
    iteamIds: '',
    commentIds: '',
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess('');
    setError('');
    try {
      // Convertir les champs iteamIds et commentIds en tableaux
      const payload = {
        ...form,
        iteamIds: form.iteamIds
          ? form.iteamIds.split(',').map((id) => id.trim())
          : [],
        commentIds: form.commentIds
          ? form.commentIds.split(',').map((id) => id.trim())
          : [],
      };
      await createAuthor(payload);
      setSuccess('Auteur créé avec succès !');
      setForm({
        username: '',
        Lastname: '',
        email: '',
        name: '',
        phone: '',
        address: '',
        iteamIds: '',
        commentIds: '',
      });
    } catch (err) {
      setError('Erreur lors de la création de l\'auteur.');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Créer un auteur</h2>
      <div>
        <label>Username :</label>
        <input
          type="text"
          name="username"
          value={form.username}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Lastname :</label>
        <input
          type="text"
          name="Lastname"
          value={form.Lastname}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Email :</label>
        <input
          type="email"
          name="email"
          value={form.email}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Name :</label>
        <input
          type="text"
          name="name"
          value={form.name}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Phone :</label>
        <input
          type="text"
          name="phone"
          value={form.phone}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Address :</label>
        <input
          type="text"
          name="address"
          value={form.address}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Iteam IDs (séparés par des virgules) :</label>
        <input
          type="text"
          name="iteamIds"
          value={form.iteamIds}
          onChange={handleChange}
          placeholder="id1,id2,id3"
        />
      </div>
      <div>
        <label>Comment IDs (séparés par des virgules) :</label>
        <input
          type="text"
          name="commentIds"
          value={form.commentIds}
          onChange={handleChange}
          placeholder="id1,id2"
        />
      </div>
      <button type="submit" disabled={loading}>
        {loading ? 'Création...' : 'Créer'}
      </button>
      {success && <div style={{ color: 'green' }}>{success}</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
    </form>
  );
};

export default AuthorCreateForm;
