import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getAllArticles } from '../../api/blog/query/';
import '../../assets/css/blog.css';

const BlogListComponent = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        setLoading(true);
        const data = await getAllArticles();
        setArticles(Array.isArray(data) ? data : []);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching articles:', error);
        setError('Impossible de charger les articles. Veuillez réessayer plus tard.');
        setLoading(false);
      }
    };
    fetchArticles();
  }, []);

  if (loading) return <div className="loading">Chargement des articles...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <main className="main">
      {/* Page Title */}
      <div className="page-title" data-aos="fade">
        <div className="heading">
          <div className="container">
            <div className="row d-flex justify-content-center text-center">
              <div className="col-lg-8">
                <h1>Blog</h1>
                <p className="mb-0">
                  Découvrez nos derniers articles, actualités et analyses sur la technologie, la programmation et plus encore.
                </p>
              </div>
            </div>
          </div>
        </div>
        <nav className="breadcrumbs">
          <div className="container">
            <ol>
              <li><Link to="/">Accueil</Link></li>
              <li className="current">Blog</li>
            </ol>
          </div>
        </nav>
      </div>

      {/* Blog Posts Section */}
      <section id="blog-posts" className="blog-posts section">
        <div className="container">
          <div className="row gy-4">
            {articles.length > 0 ? (
              articles.map((article) => (
                <div className="col-lg-4" key={article.id}>
                  <article>
                    <div className="post-img">
                      <img
                        src={article.imageUrl || "/assets/img/blog/blog-placeholder.jpg"}
                        alt={article.title || "Article"}
                        className="img-fluid"
                      />
                    </div>

                    <p className="post-category">{article.category || "Non classé"}</p>

                    <h2 className="title">
                      <Link to={`/blog/${article.id}`}>{article.title || "Sans titre"}</Link>
                    </h2>

                    <div className="d-flex align-items-center">
                      <img
                        src={article.author?.avatarUrl || "/assets/img/blog/blog-author.jpg"}
                        alt={article.author?.name || "Auteur"}
                        className="img-fluid post-author-img flex-shrink-0"
                      />
                      <div className="post-meta">
                        <p className="post-author">{article.author?.name || "Anonyme"}</p>
                        <p className="post-date">
                          <time dateTime={article.createdAt}>
                            {article.createdAt
                              ? new Date(article.createdAt).toLocaleDateString('fr-FR', {
                                  year: 'numeric',
                                  month: 'short',
                                  day: 'numeric'
                                })
                              : ''}
                          </time>
                        </p>
                      </div>
                    </div>
                  </article>
                </div>
              ))
            ) : (
              <div className="col-12 text-center">
                <p>Aucun article trouvé. Revenez plus tard pour découvrir de nouveaux contenus !</p>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* Blog Pagination Section */}
      {articles.length > 0 && (
        <section id="blog-pagination" className="blog-pagination section">
          <div className="container">
            <div className="d-flex justify-content-center">
              <ul className="pagination">
                <li className="page-item"><a href="#" className="page-link"><i className="bi bi-chevron-left"></i></a></li>
                <li className="page-item"><a href="#" className="page-link active">1</a></li>
                <li className="page-item"><a href="#" className="page-link">2</a></li>
                <li className="page-item"><a href="#" className="page-link">3</a></li>
                <li className="page-item"><a href="#" className="page-link"><i className="bi bi-chevron-right"></i></a></li>
              </ul>
            </div>
          </div>
        </section>
      )}
    </main>
  );
};

export default BlogListComponent;
